// Fetch data from Google Sheets Node
const inputData = $input.all(); // Automatically takes input from the previous node

// Function to parse date strings into Date objects
function parseDate(dateStr) {
    return new Date(dateStr);
}

// Function to remove â‚¹ symbol and convert to integer
function parsePayments(paymentStr) {
    return parseInt(paymentStr.replace("â‚¹", "").trim()) || 0;
}

// Convert data types where necessary
const data = inputData.map(member => ({
    Member_ID: parseInt(member.json["Member ID"]),
    Name: member.json["Name"],
    Age: parseInt(member.json["Age"]),
    Gender: member.json["Gender"],
    Membership_Type: member.json["Membership Type"],
    Join_Date: parseDate(member.json["Join Date"]),
    Last_Visit: parseDate(member.json["Last Visit"]),
    Total_Visits: parseInt(member.json["Total Visits"]),
    Payments_Due: parsePayments(member.json["Payments Due"])
}));

// 1ï¸âƒ£ Total Members
const totalMembers = data.length;

// 2ï¸âƒ£ Active Members in Last 7 Days
const oneWeekAgo = new Date();
oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
const activeMembers = data.filter(member => member.Last_Visit >= oneWeekAgo).length;

// 3ï¸âƒ£ Average Visits Per Member
const averageVisits = data.reduce((sum, member) => sum + member.Total_Visits, 0) / totalMembers;

// 4ï¸âƒ£ Pending Payments Summary
const totalPendingPayments = data.reduce((sum, member) => sum + member.Payments_Due, 0);
const membersWithDues = data.filter(member => member.Payments_Due > 0).length;

// ğŸ“Œ Returning a single summary object
return [{
    json: {
        "Total Members": totalMembers,
        "Active Members (Last 7 Days)": activeMembers,
        "Average Visits Per Member": averageVisits.toFixed(2),
        "Total Pending Payments (â‚¹)": totalPendingPayments,
        "Members with Pending Payments": membersWithDues
    }
}];

