// Function to extract emails, phone numbers, addresses, and websites from text
function extractData(text) {
    // Regular expressions for email, phone numbers, and websites
    const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g;
    const phoneRegex = /\b\d{10,12}\b/g; // Matches 10-12 digit phone numbers

    // Better address regex - this is a generic pattern that may need adjustment based on your data
    const addressRegex = /\b\d+\s+[A-Za-z0-9\s,\.]+(?:Avenue|Lane|Road|Boulevard|Drive|Street|Ave|Ln|Rd|Blvd|Dr|St)\.?(?:\s+[A-Za-z]+)?(?:,\s*[A-Za-z]+)?(?:,\s*[A-Z]{2})?\s*\d{5}(?:-\d{4})?\b/gi;

    const websiteRegex = /https?:\/\/(?:www\.)?([A-Za-z0-9](?:[A-Za-z0-9-]*[A-Za-z0-9])?(?:\.[A-Za-z0-9](?:[A-Za-z0-9-]*[A-Za-z0-9])?)+)(?:\/[^\s"')]*)?/g;

    // Extracting data
    const emails = [...new Set(text.match(emailRegex) || [])];
    const phoneNumbers = [...new Set(text.match(phoneRegex) || [])];
    const addresses = [...new Set((text.match(addressRegex) || []).map(addr => addr.trim()))];

    // Social media domains list
    const socialMediaDomains = ['facebook.com', 'instagram.com', 'twitter.com', 'linkedin.com', 'youtube.com', 'pinterest.com', 'tiktok.com'];

    // Website extraction
    const mainWebsites = [];
    const socialMediaLinks = {};
    const uniqueMainWebsites = new Set();
    const seenUrlPatterns = new Set();

    let match;
    while ((match = websiteRegex.exec(text)) !== null) {
        const fullUrl = match[0];
        const domain = match[1];

        // Skip resource files
        if (!/\.(jpg|jpeg|png|gif|svg|webp|js|css|ico|woff|woff2|ttf|eot)(\?.*)?$/i.test(fullUrl)) {
            // Get base URL (without query parameters and fragments)
            const baseUrl = fullUrl.split(/[?#]/)[0];

            // Check if it's a social media link
            const socialMediaPlatform = socialMediaDomains.find(socialdomain => domain.includes(socialdomain));

            if (socialMediaPlatform) {
                // Extract platform name (e.g., 'facebook' from 'facebook.com')
                const platformName = socialMediaPlatform.split('.')[0];

                // Store only the first instance of each social platform
                if (!socialMediaLinks[platformName]) {
                    socialMediaLinks[platformName] = baseUrl;
                }
            } else {
                // Process main websites
                // Create a pattern by removing repeating number patterns
                const urlPattern = baseUrl.replace(/(-\d+)+$/, '');

                // Only add if we haven't seen this pattern before
                if (!seenUrlPatterns.has(urlPattern)) {
                    seenUrlPatterns.add(urlPattern);

                    // If we haven't already added this exact URL
                    if (!uniqueMainWebsites.has(baseUrl)) {
                        uniqueMainWebsites.add(baseUrl);
                        mainWebsites.push({
                            url: baseUrl,
                            domain: domain
                        });
                    }
                }
            }
        }
    }

    return {
        emails: emails,
        phoneNumbers: phoneNumbers,
        socialMediaLinks: socialMediaLinks,
        addresses: addresses,
        mainWebsites: mainWebsites
    };
}

